INSERT INTO `h7E2w_posts` VALUES("513", "1", "2023-12-18 16:23:09", "2023-12-18 09:23:09", "[page_header height=\"350px\" title_size=\"xxlarge\" bg=\"347\" bg_size=\"original\" bg_overlay=\"rgba(0,0,0,.5)\" parallax=\"7\"]\r\n\r\n[section bg_color=\"rgb(255, 246, 252)\" padding=\"80px\" padding__sm=\"40px\"]\r\n\r\n[row]\r\n\r\n[col span__sm=\"12\"]\r\n\r\n[ux_text font_size=\"1.4\" font_size__sm=\"1.3\"]\r\n\r\n<h2 class=\"uppercase lts-04 fw-800 pd17 head_div\">Gii thiu v cm tm</h2>\r\n[/ux_text]\r\n\r\n[/col]\r\n\r\n[/row]\r\n[row]\r\n\r\n[col span=\"3\" span__sm=\"12\"]\r\n\r\n\r\n[/col]\r\n[col span=\"4\" span__sm=\"12\"]\r\n\r\n[ux_text font_size=\"1.1\" font_size__sm=\"1\"]\r\n\r\n<p class=\"jtf\">Cm Tm ra i vi mong mun mang n nhng tri nghim m thc tuyt vi v y  dinh dng trong mi ba n hng ngy. Chng ti t ho vi vic p dng cng ngh Nht Bn  to ra nhng ba n mm ngon v lnh mnh t nhng nguyn liu cht lng.</p>\r\n[/ux_text]\r\n\r\n[/col]\r\n[col span=\"4\" span__sm=\"12\"]\r\n\r\n[ux_text font_size=\"1.1\" font_size__sm=\"1\"]\r\n\r\n<p class=\"jtf\">Cm Tm  tri qua hnh trnh vi nhng chic xe lu ng mu tm hin din trn khp cc con ng ti TP. HCM. Chng ti t tin rng c th p ng nhu cu m thc ca tt c mi ngi. Cho d bn l ngi a chung m thc chay hay mn, tr tui hay cao tui, bn rn hay chm sc sc khe, Cm Tm lun sn sng phc v bn  mi lc mi ni.</p>\r\n[/ux_text]\r\n\r\n[/col]\r\n[col span__sm=\"12\"]\r\n\r\n[ux_banner height=\"400px\" bg=\"442\" bg_size=\"original\" bg_pos=\"51% 49%\"]\r\n\r\n[text_box width__sm=\"60\" position_x=\"50\" position_y=\"50\"]\r\n\r\n\r\n[/text_box]\r\n\r\n[/ux_banner]\r\n\r\n[/col]\r\n\r\n[/row]\r\n\r\n[/section]", "Nhng quyn", "", "inherit", "closed", "closed", "", "27-revision-v1", "", "", "2023-12-18 16:23:09", "2023-12-18 09:23:09", "", "27", "http://comtim.vn/?p=513", "0", "revision", "", "0");
INSERT INTO `h7E2w_posts` VALUES("514", "1", "2023-12-18 16:25:22", "2023-12-18 09:25:22", "[page_header height=\"350px\" title_size=\"xxlarge\" bg=\"347\" bg_size=\"original\" bg_overlay=\"rgba(0,0,0,.5)\" parallax=\"7\"]\n\n[section bg_color=\"rgb(255, 246, 252)\" padding=\"80px\" padding__sm=\"40px\"]\n\n[row]\n\n[col span__sm=\"12\"]\n\n[ux_text font_size=\"1.4\" font_size__sm=\"1.3\"]\n\n<h2 class=\"uppercase lts-04 fw-800 pd17 head_div\">HP TC NHNG QUYN</h2>\n\n[/ux_text]\n\n[/col]\n\n[/row]\n[row]\n\n[col span=\"3\" span__sm=\"12\"]\n\n[ux_text font_size=\"1.2\" text_color=\"#551d47\"]\n\n<h3 class=\"uppercase\">HP TC, XY DNG V PHT TRIN CNG NHAU</h3>\n\n[/ux_text]\n\n[/col]\n[col span=\"4\" span__sm=\"12\"]\n\n[ux_text font_size=\"1.1\" font_size__sm=\"1\"]\n\n<p class=\"jtf\">Kt ni v hp tc cht ch: Cm Tm xem hp tc nhng quyn nh mt cuc hnh ng chung, chng ti khng ch l nh cung cp m cn l i tc ng hnh cng bn. Chng ti tn trng v lng nghe  kin ca i tc v lun tm cch cng pht trin.</p>\n\n[/ux_text]\n\n[/col]\n[col span=\"4\" span__sm=\"12\"]\n\n[ux_text font_size=\"1.1\" font_size__sm=\"1\"]\n\n<p class=\"jtf\">H tr ton din: Cm Tm  hiu rng s thnh cng ca i tc cng l s thnh cng ca chng ti. i ng ca chng ti lun sn sng h tr bn, t vic khi nghip kinh doanh n vic duy tr v pht trin m hnh nhng quyn.\n</p>\n<p class=\"jtf\">Xy dng v pht trin bn vng: Chng ti xy dng mi quan h lu di, ng tin cy vi cc i tc nhng quyn. Chng ti cam kt cng nhau pht trin v cung cp s hi lng cho khch hng.</p>\n\n[/ux_text]\n\n[/col]\n\n[/row]\n\n[/section]", "Nhng quyn", "", "inherit", "closed", "closed", "", "27-revision-v1", "", "", "2023-12-18 16:25:22", "2023-12-18 09:25:22", "", "27", "http://comtim.vn/?p=514", "0", "revision", "", "0");
