INSERT INTO `h7E2w_wc_admin_notes` VALUES("51", "woocommerce-shipping-fedex-api-outage-2023-07-16", "warning", "en_US", "Scheduled FedEx API outage  July 2023", "On July 16 there will be a full outage of the FedEx API from 04:00 to 08:00 AM UTC. Due to planned maintenance by FedEx, you\'ll be unable to provide FedEx shipping rates during this time. Follow the link below for more information and recommendations on how to minimize impact.", "{}", "pending", "woocommerce.com", "2023-12-15 04:58:49", NULL, "0", "plain", "", "0", "0", "info");
