INSERT INTO `h7E2w_wc_admin_notes` VALUES("55", "woocommerce-WooPayments-Aug-2023-update-needed", "update", "en_US", "Action required: Security update for WooPayments (WooCommerce Payments) plugin", "<strong>Your shop requires an important security update for the WooPayments (WooCommerce Payments) extension</strong>. The fix for this vulnerability was released on July 31. Please update immediately.", "{}", "pending", "woocommerce.com", "2023-12-15 04:58:49", NULL, "0", "plain", "", "0", "0", "info");
