INSERT INTO `h7E2w_wc_admin_notes` VALUES("63", "amazon-mcf-reviews-2023-12-07", "marketing", "en_US", "Enjoying Amazon MCF for WooCommerce?", "We\'re Never Settle, the developers behind Amazon MCF for WooCommerce, and would be deeply honored to have your review. Reviews help immensely as other users can learn how MCF can solve their needs too! Not happy or need help? Please reach out for support and wed love to make things right!", "{}", "pending", "woocommerce.com", "2023-12-15 04:58:49", NULL, "0", "plain", "", "0", "0", "info");
