INSERT INTO `h7E2w_wc_admin_notes` VALUES("65", "wc-admin-choosing-a-theme", "marketing", "en_US", "Choosing a theme?", "Check out the themes that are compatible with WooCommerce and choose one aligned with your brand and business needs.", "{}", "unactioned", "woocommerce-admin", "2023-12-16 04:58:53", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `h7E2w_wc_admin_notes` VALUES("66", "wc-admin-customizing-product-catalog", "info", "en_US", "How to customize your product catalog", "You want your product catalog and images to look great and align with your brand. This guide will give you all the tips you need to get your products looking great in your store.", "{}", "unactioned", "woocommerce-admin", "2023-12-17 05:05:04", NULL, "0", "plain", "", "0", "0", "info");
INSERT INTO `h7E2w_wc_admin_notes` VALUES("67", "wc-admin-mobile-app", "info", "en_US", "Install Woo mobile app", "Install the WooCommerce mobile app to manage orders, receive sales notifications, and view key metrics  wherever you are.", "{}", "unactioned", "woocommerce-admin", "2023-12-17 05:05:04", NULL, "0", "plain", "", "0", "0", "info");
