INSERT INTO `h7E2w_posts` VALUES("513", "1", "2023-12-18 16:23:09", "2023-12-18 09:23:09", "[page_header height=\"350px\" title_size=\"xxlarge\" bg=\"347\" bg_size=\"original\" bg_overlay=\"rgba(0,0,0,.5)\" parallax=\"7\"]\r\n\r\n[section bg_color=\"rgb(255, 246, 252)\" padding=\"80px\" padding__sm=\"40px\"]\r\n\r\n[row]\r\n\r\n[col span__sm=\"12\"]\r\n\r\n[ux_text font_size=\"1.4\" font_size__sm=\"1.3\"]\r\n\r\n<h2 class=\"uppercase lts-04 fw-800 pd17 head_div\">Giới thiệu về cơm tím</h2>\r\n[/ux_text]\r\n\r\n[/col]\r\n\r\n[/row]\r\n[row]\r\n\r\n[col span=\"3\" span__sm=\"12\"]\r\n\r\n\r\n[/col]\r\n[col span=\"4\" span__sm=\"12\"]\r\n\r\n[ux_text font_size=\"1.1\" font_size__sm=\"1\"]\r\n\r\n<p class=\"jtf\">Cơm Tím ra đời với mong muốn mang đến những trải nghiệm ẩm thực tuyệt vời và đầy đủ dinh dưỡng trong mỗi bữa ăn hàng ngày. Chúng tôi tự hào với việc áp dụng công nghệ Nhật Bản để tạo ra những bữa ăn mềm ngon và lành mạnh từ những nguyên liệu chất lượng.</p>\r\n[/ux_text]\r\n\r\n[/col]\r\n[col span=\"4\" span__sm=\"12\"]\r\n\r\n[ux_text font_size=\"1.1\" font_size__sm=\"1\"]\r\n\r\n<p class=\"jtf\">Cơm Tím đã trải qua hành trình với những chiếc xe lưu động màu tím hiện diện trên khắp các con đường tại TP. HCM. Chúng tôi tự tin rằng có thể đáp ứng nhu cầu ẩm thực của tất cả mọi người. Cho dù bạn là người ưa chuộng ẩm thực chay hay mặn, trẻ tuổi hay cao tuổi, bận rộn hay chăm sóc sức khỏe, Cơm Tím luôn sẵn sàng phục vụ bạn ở mọi lúc mọi nơi.</p>\r\n[/ux_text]\r\n\r\n[/col]\r\n[col span__sm=\"12\"]\r\n\r\n[ux_banner height=\"400px\" bg=\"442\" bg_size=\"original\" bg_pos=\"51% 49%\"]\r\n\r\n[text_box width__sm=\"60\" position_x=\"50\" position_y=\"50\"]\r\n\r\n\r\n[/text_box]\r\n\r\n[/ux_banner]\r\n\r\n[/col]\r\n\r\n[/row]\r\n\r\n[/section]", "Nhượng quyền", "", "inherit", "closed", "closed", "", "27-revision-v1", "", "", "2023-12-18 16:23:09", "2023-12-18 09:23:09", "", "27", "http://comtim.vn/?p=513", "0", "revision", "", "0");
INSERT INTO `h7E2w_posts` VALUES("514", "1", "2023-12-18 16:25:22", "2023-12-18 09:25:22", "[page_header height=\"350px\" title_size=\"xxlarge\" bg=\"347\" bg_size=\"original\" bg_overlay=\"rgba(0,0,0,.5)\" parallax=\"7\"]\n\n[section bg_color=\"rgb(255, 246, 252)\" padding=\"80px\" padding__sm=\"40px\"]\n\n[row]\n\n[col span__sm=\"12\"]\n\n[ux_text font_size=\"1.4\" font_size__sm=\"1.3\"]\n\n<h2 class=\"uppercase lts-04 fw-800 pd17 head_div\">HỢP TÁC NHƯỢNG QUYỀN</h2>\n\n[/ux_text]\n\n[/col]\n\n[/row]\n[row]\n\n[col span=\"3\" span__sm=\"12\"]\n\n[ux_text font_size=\"1.2\" text_color=\"#551d47\"]\n\n<h3 class=\"uppercase\">HỢP TÁC, XÂY DỰNG VÀ PHÁT TRIỂN CÙNG NHAU</h3>\n\n[/ux_text]\n\n[/col]\n[col span=\"4\" span__sm=\"12\"]\n\n[ux_text font_size=\"1.1\" font_size__sm=\"1\"]\n\n<p class=\"jtf\">Kết nối và hợp tác chặt chẽ: Cơm Tím xem hợp tác nhượng quyền như một cuộc hành động chung, chúng tôi không chỉ là nhà cung cấp mà còn là đối tác đồng hành cùng bạn. Chúng tôi tôn trọng và lắng nghe ý kiến của đối tác và luôn tìm cách cùng phát triển.</p>\n\n[/ux_text]\n\n[/col]\n[col span=\"4\" span__sm=\"12\"]\n\n[ux_text font_size=\"1.1\" font_size__sm=\"1\"]\n\n<p class=\"jtf\">Hỗ trợ toàn diện: Cơm Tím  hiểu rằng sự thành công của đối tác cũng là sự thành công của chúng tôi. Đội ngũ của chúng tôi luôn sẵn sàng hỗ trợ bạn, từ việc khởi nghiệp kinh doanh đến việc duy trì và phát triển mô hình nhượng quyền.\n</p>\n<p class=\"jtf\">Xây dựng và phát triển bền vững: Chúng tôi xây dựng mối quan hệ lâu dài, đáng tin cậy với các đối tác nhượng quyền. Chúng tôi cam kết cùng nhau phát triển và cung cấp sự hài lòng cho khách hàng.</p>\n\n[/ux_text]\n\n[/col]\n\n[/row]\n\n[/section]", "Nhượng quyền", "", "inherit", "closed", "closed", "", "27-revision-v1", "", "", "2023-12-18 16:25:22", "2023-12-18 09:25:22", "", "27", "http://comtim.vn/?p=514", "0", "revision", "", "0");
