INSERT INTO `h7E2w_wc_admin_notes` VALUES("61", "tap_to_pay_iphone_q4_2023_no_woopayments", "marketing", "en_US", "Accept in-person contactless payments on your iPhone", "Tap to Pay on iPhone and WooPayments is quick, secure, and simple to set up — no extra terminals or card readers are needed. Accept contactless debit and credit cards, Apple Pay, and other NFC digital wallets in person.", "{}", "pending", "woocommerce.com", "2023-12-15 04:58:49", NULL, "0", "plain", "", "0", "0", "info");
