INSERT INTO `h7E2w_wc_admin_notes` VALUES("69", "wc-admin-usage-tracking-opt-in", "info", "en_US", "Help WooCommerce improve with usage tracking", "Gathering usage data allows us to improve WooCommerce. Your store will be considered as we evaluate new features, judge the quality of an update, or determine if an improvement makes sense. You can always visit the <a href=\"https://comtim.vn/wp-admin/admin.php?page=wc-settings&#038;tab=advanced&#038;section=woocommerce_com\" target=\"_blank\">Settings</a> and choose to stop sharing data. <a href=\"https://woo.com/usage-tracking?utm_medium=product\" target=\"_blank\">Read more</a> about what data we collect.", "{}", "unactioned", "woocommerce-admin", "2023-12-22 07:47:41", NULL, "0", "plain", "", "0", "0", "info");
